function setBodyLoaded(){document.getElementsByTagName("body")[0].classList.add("loaded")}function setAnimatedBlockLoaded(){const e=document.getElementById("animatedBlock");if(!e)return;let d=!1;window.addEventListener("scroll",()=>{window.pageYOffset+window.innerHeight>e.offsetTop+300&&(d||(e.classList.add("loaded"),d=!0))})}window.addEventListener("load",(function(){setBodyLoaded(),setAnimatedBlockLoaded()}));